include <cs50.h>
include <stdio.h>
include <math.h>
int main(void)
{   
    float x;
    do{
        printf("O hai! How much change is owed\n");
        x = GetFloat();
    }while(x<0);
    x = round(x*100)/100;
    int y = round(100*x);
    int count = 0;
    count = count + y/25;
    y = y%25;
    count = count + y/10;
    y = y%10;
    count = count + y/5;
    y = y%5;
    count = count + y/1;
    printf("%i\n",count);
}    

