include <stdio.h>
include <cs50.h>
include <string.h>
include <ctype.h>
int main(int argc, string argv[])
{
    if (argc !=2 ) 
    {
        printf("Usage: ./caesar k \n");
        return 1;
    }
    
    int n = strlen(argv[1]);
    int count;
    string key = argv[1];
    
    for(int i=0; i<n ;i++)
    {
        if (isalpha(key[i]) == false)
        {
            break;
        }
        count = i;
    }
    
    if(count != n-1) 
    {
        printf("Usage: ./caesar k \n");
        return 1;  
    }
    
    printf("plaintext:   ");
    string p = get_string();
    printf("ciphertext:  ");
    int j = 0;
    for(int i=0;i<strlen(p);i++)
    {
        if isalpha(p[i])
        {
            if isupper(key[j%n])
            {
                if isupper(p[i])
                {
                    p[i] = (p[i] - 65 + key[j%n] - 65)%26 + 65;
                }
                if islower(p[i])
                {
                    p[i] = (p[i] - 97 + key[j%n] - 65)%26 + 97;
                }
            }
            if islower(key[j%n])
            {
                if isupper(p[i])
                {
                    p[i] = (p[i] - 65 + key[j%n] - 97)%26 + 65;
                }
                if islower(p[i])
                {
                    p[i] = (p[i] - 97 + key[j%n] - 97)%26 + 97;
                }
            }
            j = j + 1;
        }
        printf("%c",p[i]);
    }
    printf("\n");
}    

